	public final  function getPosts() {
		$listPosts = [];

		$db = $this->dbConnect();
		$q = $db->query('SELECT id, image, title, text, date, tag, status FROM posts ORDER BY date DESC');

		while ($data = $q->fetch(PDO::FETCH_ASSOC))
		{
			$listPosts[] = new Post($data);
		}

		return $listPosts;
	}


