	public final  function updatePost(Post $post) {

		$db = $this->dbConnect();
		$q = $db->prepare('UPDATE posts SET image = :image, title = :title, text = :text; date = :date, tag = :tag, status = :status WHERE id = :id');

		$q->bindValue(':id', $post->id(), PDO::PARAM_INT);
